import { IoIosMail } from "react-icons/io";
import { Link } from "react-router-dom";
import { useState } from "react";

export default function ForgetPassword() {
  const [email, setEmail] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Reset link sent to:", email);
    // Ajoute ici la logique pour envoyer l'email de réinitialisation
  };

  return (
    <div className="w-full max-w-md mx-auto mt-10 p-6 bg-white shadow-lg rounded-lg">
      <h2 className="text-2xl font-bold text-center text-blueGray-700 mb-4">
        Forgot Password?
      </h2>
      <p className="text-center text-gray-500 mb-6">
        Enter your email and we'll send you a link to reset your password.
      </p>

      <form onSubmit={handleSubmit}>
        {/* Email Field */}
        <div className="relative w-full mb-4">
          <label className="block uppercase text-blueGray-600 text-xs font-bold mb-2">
            Email
          </label>
          <div className="flex items-center border border-gray-300 rounded-lg shadow-sm px-3 py-2 bg-white">
            <IoIosMail className="text-blueGray-600 text-xl" />
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="border-0 px-3 py-2 placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm focus:outline-none focus:ring w-full transition-all duration-150"
              placeholder="Enter your email"
              required
            />
          </div>
        </div>

        {/* Submit Button */}
        <div className="text-center mt-6">
          <button
            type="submit"
            className="bg-blue-600 text-white active:bg-blue-700 text-sm font-bold uppercase px-6 py-3 rounded shadow hover:shadow-lg outline-none focus:outline-none w-full transition-all duration-150"
          >
            Send Reset Link
          </button>
        </div>
      </form>

      {/* Links to Login & Register */}
      <div className="flex justify-between mt-6 text-sm">
        <Link to="/auth/login" className="text-blue-600 hover:underline">
          Remember your password? <span className="font-semibold">Sign In</span>
        </Link>
        <Link to="/auth/register" className="text-blue-600 hover:underline">
          <span className="font-semibold">Create new account</span>
        </Link>
      </div>
    </div>
  );
}
