import { useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

export default function SearchBar() {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [location, setLocation] = useState("");
  const [activity, setActivity] = useState("");

  return (
    <div className="container mx-auto px-4 pb-32 pt-48">
      <div className="flex flex-col md:flex-row items-center bg-white p-6 rounded-lg shadow-lg">
        {/* Localisation Input */}
        <div className="flex-1 m-2">
          <label className="block text-gray-700 font-bold mb-2">Localisation</label>
          <input
            type="text"
            className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
            placeholder="Entrez une localisation"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
          />
        </div>
        
        {/* Activity Dropdown */}
        <div className="flex-1 m-2">
          <label className="block text-gray-700 font-bold mb-2">Activité</label>
          <select
            className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
            value={activity}
            onChange={(e) => setActivity(e.target.value)}
          >
            <option value="">Sélectionnez une activité</option>
            <option value="sport">Sport</option>
            <option value="culture">Culture</option>
            <option value="loisir">Loisir</option>
          </select>
        </div>

        {/* Date Picker */}
        <div className="flex-1 m-2">
          <label className="block text-gray-700 font-bold mb-2">Quand ?</label>
          <DatePicker
            selected={selectedDate}
            onChange={(date) => setSelectedDate(date)}
            className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
          />
        </div>

        {/* Search Button */}
        <div className="flex-1 m-2">
          <button className="w-full bg-green-500 text-white font-bold py-3 px-6 rounded-lg hover:bg-green-600">
            Rechercher
          </button>
        </div>
      </div>
    </div>
  );
}
