import React, { useState } from "react";
import { IoIosMail } from "react-icons/io";
import { Link } from "react-router-dom";

export default function Forget() {
  const [email, setEmail] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Reset link sent to:", email);
    // Logique pour envoyer l'email de réinitialisation
  };

  return (
    <div className="container mx-auto px-4 h-full">
      <div className="flex content-center items-center justify-center h-full">
        <div className="w-full lg:w-4/12 px-4">
          <div className="relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded-lg bg-blueGray-200 border-0">
            <div className="rounded-t mb-0 px-6 py-6">
              <div className="text-center mb-3">
                <h6 className="text-blueGray-500 text-sm font-bold">
                  Forgot Password?
                </h6>
              </div>
              <hr className="mt-6 border-b-1 border-blueGray-300" />
            </div>

            <div className="flex-auto px-4 lg:px-10 py-10 pt-0">
              <div className="text-blueGray-400 text-center mb-3 font-bold">
                <small>Enter your email to reset your password</small>
              </div>
              <form onSubmit={handleSubmit}>
                {/* Email Field */}
                <div className="relative w-full mb-3">
                  <label className="block uppercase text-blueGray-600 text-xs font-bold mb-2">
                    Email
                  </label>
                  <div className="flex items-center border border-gray-300 rounded-lg shadow-sm px-3 py-2 bg-white">
                    <IoIosMail className="text-blueGray-600 text-xl" />
                    <input
                      type="email"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      className="border-0 px-3 py-2 placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm focus:outline-none focus:ring w-full transition-all duration-150"
                      placeholder="Enter your email"
                      required
                    />
                  </div>
                </div>

                {/* Submit Button */}
                <div className="text-center mt-6">
                  <button
                    type="submit"
                    className="bg-blue-600 text-white active:bg-blue-700 text-sm font-bold uppercase px-6 py-3 rounded shadow hover:shadow-lg outline-none focus:outline-none w-full transition-all duration-150"
                  >
                    Send Reset Link
                  </button>
                </div>
              </form>

              {/* Links for Login and Register */}
              <div className="flex justify-between mt-6 text-sm">
                <Link to="/auth/login" className="text-blue-600 hover:underline hover:text-blue-800">
                  Remember your password? <span className="font-semibold">Sign In</span>
                </Link>
                <Link to="/auth/register" className="text-blue-600 hover:underline hover:text-blue-800">
                  <span className="font-semibold">Create new account</span>
                </Link>
              </div>

            </div> {/* Fin du contenu principal */}
          </div> {/* Fin du card */}
        </div> {/* Fin de la colonne */}
      </div> {/* Fin du container flex */}
    </div>
  );
}
