import React, { useState } from "react";

const SearchBar = () => {
  const [location, setLocation] = useState("");
  const [date, setDate] = useState("");

  const handleSearch = () => {
    console.log("Recherche effectuée avec :", { location, date });
    // Tu peux ajouter ici une fonction pour gérer la recherche
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-lg flex flex-col md:flex-row items-center gap-4">
      {/* Champ de localisation */}
      <input
        type="text"
        placeholder="Entrez votre localisation..."
        value={location}
        onChange={(e) => setLocation(e.target.value)}
        className="border p-2 rounded w-full md:w-1/3"
      />

      {/* Sélecteur de date */}
      <input
        type="date"
        value={date}
        onChange={(e) => setDate(e.target.value)}
        className="border p-2 rounded w-full md:w-1/3"
      />

      {/* Bouton de recherche */}
      <button
        onClick={handleSearch}
        className="bg-green-600 text-white px-4 py-2 rounded w-full md:w-auto"
      >
        Rechercher
      </button>
    </div>
  );
};

export default SearchBar;
