import React, { useState } from "react";
import { FaSwimmer, FaFutbol, FaBasketballBall, FaTableTennis, FaMapMarkerAlt, FaSearch } from "react-icons/fa";

const SearchBar = () => {
    const [activity, setActivity] = useState("");
    const [location, setLocation] = useState("");
    const [date, setDate] = useState("");
    const [showDropdown, setShowDropdown] = useState(false);
    const [showLocationDropdown, setShowLocationDropdown] = useState(false);

    const activities = [
        { value: "natation", label: "Natation", icon: <FaSwimmer className="text-blue-500" /> },
        { value: "football", label: "Football", icon: <FaFutbol className="text-green-500" /> },
        { value: "basketball", label: "Basketball", icon: <FaBasketballBall className="text-orange-500" /> },
        { value: "tennis", label: "Tennis", icon: <FaTableTennis className="text-red-500" /> },
    ];

    const cities = [
        { value: "tunis", label: "Tunis", icon: <FaMapMarkerAlt className="text-gray-500" /> },
        { value: "ariana", label: "Ariana", icon: <FaMapMarkerAlt className="text-gray-500" /> },
        { value: "ben_arous", label: "Ben Arous", icon: <FaMapMarkerAlt className="text-gray-500" /> },
        { value: "mannouba", label: "Manouba", icon: <FaMapMarkerAlt className="text-gray-500" /> },
    ];

    const handleSearch = () => {
        console.log("Recherche effectuée avec :", { activity, location, date });
    };

    return (
        <div className="bg-white p-4 rounded-lg shadow-xl max-w-sm mx-auto"> {/* Réduit le padding et max-w */}
            <h2 className="text-xl font-semibold mb-4 text-gray-800 text-center">Rechercher</h2> {/* Réduit la taille du titre */}

            {/* Sélecteur d'activité */}
            <div className="mb-3"> {/* Réduit la marge */}
                <label className="block text-sm font-medium mb-1">Activité</label> {/* Réduit la taille de la police */}
                <div className="relative">
                    <button
                        onClick={() => setShowDropdown(!showDropdown)}
                        className="border p-2 pl-3 pr-6 rounded-lg w-full text-left focus:outline-none focus:ring-2 focus:ring-emerald-500 cursor-pointer flex items-center justify-between text-sm" // Réduit le padding et la taille de la police
                    >
                        {activity
                            ? activities.find((act) => act.value === activity).icon
                            : <FaSearch className="text-gray-400" />}
                        <span className="ml-2">
                            {activity
                                ? activities.find((act) => act.value === activity).label
                                : "Activité"}
                        </span>
                    </button>

                    {showDropdown && (
                        <ul className="absolute top-full left-0 w-full bg-white border rounded-lg mt-1 shadow-md z-10 text-sm"> {/* Réduit la marge et la taille de la police */}
                            {activities.map((act) => (
                                <li
                                    key={act.value}
                                    onClick={() => {
                                        setActivity(act.value);
                                        setShowDropdown(false);
                                    }}
                                    className="p-2 flex items-center gap-2 cursor-pointer hover:bg-gray-100" // Réduit le padding
                                >
                                    {act.icon} {act.label}
                                </li>
                            ))}
                        </ul>
                    )}
                </div>
            </div>

            {/* Sélecteur de localisation */}
            <div className="mb-3"> {/* Réduit la marge */}
                <label className="block text-sm font-medium mb-1">Localisation</label> {/* Réduit la taille de la police */}
                <div className="relative">
                    <button
                        onClick={() => setShowLocationDropdown(!showLocationDropdown)}
                        className="border p-2 pl-3 pr-6 rounded-lg w-full text-left focus:outline-none focus:ring-2 focus:ring-emerald-500 cursor-pointer flex items-center justify-between text-sm" // Réduit le padding et la taille de la police
                    >
                        {location
                            ? cities.find((city) => city.value === location).icon
                            : <FaMapMarkerAlt className="text-gray-400" />}
                        <span className="ml-2">
                            {location
                                ? cities.find((city) => city.value === location).label
                                : "Localisation"}
                        </span>
                    </button>

                    {showLocationDropdown && (
                        <ul className="absolute top-full left-0 w-full bg-white border rounded-lg mt-1 shadow-md z-10 text-sm"> {/* Réduit la marge et la taille de la police */}
                            {cities.map((city) => (
                                <li
                                    key={city.value}
                                    onClick={() => {
                                        setLocation(city.value);
                                        setShowLocationDropdown(false);
                                    }}
                                    className="p-2 flex items-center gap-2 cursor-pointer hover:bg-gray-100" // Réduit le padding
                                >
                                    {city.icon} {city.label}
                                </li>
                            ))}
                        </ul>
                    )}
                </div>
            </div>

            {/* Sélecteur de date */}
            <div className="mb-3"> {/* Réduit la marge */}
                <label className="block text-sm font-medium mb-1">Date</label> {/* Réduit la taille de la police */}
                <div className="relative">
                    <input
                        type="date"
                        value={date}
                        onChange={(e) => setDate(e.target.value)}
                        className="border p-2 pl-3 rounded-lg w-full focus:outline-none focus:ring-2 focus:ring-emerald-500 cursor-pointer text-sm" // Réduit le padding et la taille de la police
                    />
                </div>
            </div>

            {/* Bouton de recherche */}
            <button
                onClick={handleSearch}
                className="bg-emerald-600 px-4 py-2 rounded-lg hover:bg-emerald-500 transition-all duration-200 w-full flex items-center justify-center text-sm" // Réduit le padding et la taille de la police
            >
                <FaSearch className="mr-2" /> Rechercher
            </button>
        </div>
    );
};

export default SearchBar;
