import React, { useState } from "react";
import { FaSwimmer, FaFutbol, FaBasketballBall, FaTableTennis, FaMapMarkerAlt, FaCalendarAlt, FaSearch } from "react-icons/fa";
const SearchBar = () => {
  const [activity, setActivity] = useState("");
  const [location, setLocation] = useState("");
  const [date, setDate] = useState("");
  const [isOpen, setIsOpen] = useState(false);

  const handleSearch = () => {
    console.log("Recherche effectu√©e avec :", { activity, location, date });
    // Ajoutez ici la logique de recherche
  };
    // Liste des activit√©s avec leurs ic√¥nes
    const activities = [
        { value: "natation", label: "Natation", icon: <FaSwimmer className="text-blue-500" /> },
        { value: "football", label: "Football", icon: <FaFutbol className="text-green-500" /> },
        { value: "basketball", label: "Basketball", icon: <FaBasketballBall className="text-orange-500" /> },
        { value: "tennis", label: "Tennis", icon: <FaTableTennis className="text-red-500" /> },
      ];
    
      return (
        <div className="relative w-full md:w-1/4">
          <select
            value={activity}
            onChange={(e) => setActivity(e.target.value)}
            onFocus={() => setIsOpen(true)}
            onBlur={() => setTimeout(() => setIsOpen(false), 200)} // Ajoute un d√©lai pour √©viter la fermeture instantan√©e
            className="border p-3 rounded-lg w-full focus:outline-none focus:ring-2 focus:ring-emerald-500 cursor-pointer appearance-none"
          >
            <option value="">Activit√©</option>
            {activities.map((act) => (
              <option key={act.value} value={act.value}>
                {act.label}
              </option>
            ))}
          </select>
    
          {/* Affichage des ic√¥nes uniquement quand le menu est ouvert */}
          {isOpen && (
            <div className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-500">
              {activities.find((act) => act.value === activity)?.icon}
            </div>
          )}
     
      

        {/* S√©lecteur de localisation */}
        <select
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="border p-3 rounded-lg w-full md:w-1/4 focus:outline-none focus:ring-2 focus:ring-emerald-500 cursor-pointer"
        >
          <option value="">Localisation</option>
          <option value="tunis">Tunis</option>
          <option value="ariana">Ariana</option>
          <option value="ben_arous">Ben Arous</option>
          <option value="mannouba">Manouba</option>
        </select>

        {/* S√©lecteur de date */}
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          className="border p-3 rounded-lg w-full md:w-1/4 focus:outline-none focus:ring-2 focus:ring-emerald-500 cursor-pointer"
        />

        {/* Bouton de recherche */}
        <button
          onClick={handleSearch}
          className="bg-emerald-600  px-6 py-3 rounded-lg hover: bg-emerald-500 transition-all duration-200 w-full md:w-auto cursor-pointer"
        >
          üìå Rechercher
        </button>
      </div>
   
  );
};

export default SearchBar;
