import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import { getAllUsers, deleteUserById } from "../../services/ApiUser";

export default function CardTable({ color }) {
  const [users, setUsers] = useState([]);

  // Fonction pour récupérer les utilisateurs
  const getUsers = async () => {
    try {
      const res = await getAllUsers();
      if (res?.data?.userList) {
        setUsers(res.data.userList);
      } else {
        setUsers([]);
      }
    } catch (error) {
      console.error("Erreur lors de la récupération des utilisateurs :", error);
      setUsers([]);
    }
  };

  // Fonction pour supprimer un utilisateur
  const deleteUser = async (id) => {
    try {
      await deleteUserById(id);
      setUsers((prevUsers) => prevUsers.filter((user) => user._id !== id)); // Mise à jour locale
    } catch (error) {
      console.error("Erreur lors de la suppression :", error);
    }
  };

  useEffect(() => {
    getUsers();
  }, []);

  return (
    <div
      className={`relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded ${
        color === "light" ? "bg-white text-black" : "bg-gray-800 text-white"
      }`}
    >
      {/* En-tête */}
      <div className="rounded-t px-4 py-3 border-0 bg-gray-100">
        <div className="flex justify-between items-center">
          <h3 className="font-semibold text-lg">Liste des utilisateurs</h3>
          <button onClick={getUsers} className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700 transition">
            Rafraîchir
          </button>
        </div>
      </div>

      {/* Tableau des utilisateurs */}
      <div className="block w-full overflow-x-auto">
        <table className="w-full bg-transparent border-collapse">
          <thead>
            <tr className="bg-gray-200 text-gray-700 uppercase text-xs font-semibold">
              <th className="px-6 py-3 text-left">Prénom</th>
              <th className="px-6 py-3 text-left">Nom</th>
              <th className="px-6 py-3 text-left">Email</th>
              <th className="px-6 py-3 text-left">Créé le</th>
              <th className="px-6 py-3 text-left">Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.length > 0 ? (
              users.map((user) => (
                <tr key={user._id} className="border-b">
                  <td className="px-6 py-4">{user.firstName}</td>
                  <td className="px-6 py-4">{user.lastName}</td>
                  <td className="px-6 py-4">{user.email}</td>
                  <td className="px-6 py-4">{new Date(user.createdAt).toLocaleDateString()}</td>
                  <td className="px-6 py-4">
                    <button onClick={() => deleteUser(user._id)} className="bg-orange-500 text-white px-4 py-2 rounded hover:bg-orange-700 transition">
                      Supprimer
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5" className="text-center py-4 text-gray-500">Aucun utilisateur trouvé.</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

CardTable.defaultProps = {
  color: "light",
};

CardTable.propTypes = {
  color: PropTypes.oneOf(["light", "dark"]),
};
