import React from "react";
import { Link } from "react-router-dom";
import { createPopper } from "@popperjs/core";

const IndexDropdown = () => {
  const [dropdownPopoverShow, setDropdownPopoverShow] = React.useState(false);
  const btnDropdownRef = React.createRef();
  const popoverDropdownRef = React.createRef();

  const openDropdownPopover = () => {
    createPopper(btnDropdownRef.current, popoverDropdownRef.current, {
      placement: "bottom-start",
    });
    setDropdownPopoverShow(true);
  };

  const closeDropdownPopover = () => {
    setDropdownPopoverShow(false);
  };

  return (
    <>
      {/* Icône à la place de "Demo Pages" */}
      <button
        className="text-blue-900 text-xl px-3 py-4 lg:py-2 flex items-center"
        ref={btnDropdownRef}
        onClick={(e) => {
          e.preventDefault();
          dropdownPopoverShow ? closeDropdownPopover() : openDropdownPopover();
        }}
      >
        <i className="fas fa-ellipsis-h"></i>
      </button>

      {/* Contenu du dropdown avec fond bleu clair */}
      <div
        ref={popoverDropdownRef}
        className={`${
          dropdownPopoverShow ? "block" : "hidden"
        } bg-blue-200 text-base z-50 float-left py-2 list-none text-left rounded shadow-lg min-w-48 border border-blue-300`}
      >
        <span className="text-sm pt-2 pb-0 px-4 font-bold block w-full text-blue-900">
          Admin Layout
        </span>
        <Link
          to="/admin/dashboard"
          className="text-sm py-2 px-4 font-normal block w-full bg-transparent text-blue-900 hover:bg-blue-300"
        >
          Dashboard
        </Link>
        <Link
          to="/admin/settings"
          className="text-sm py-2 px-4 font-normal block w-full bg-transparent text-blue-900 hover:bg-blue-300"
        >
          Settings
        </Link>
        <Link
          to="/admin/tables"
          className="text-sm py-2 px-4 font-normal block w-full bg-transparent text-blue-900 hover:bg-blue-300"
        >
          Tables
        </Link>
        <Link
          to="/admin/maps"
          className="text-sm py-2 px-4 font-normal block w-full bg-transparent text-blue-900 hover:bg-blue-300"
        >
          Maps
        </Link>

        {/* Séparation */}
        <div className="h-0 mx-4 my-2 border border-solid border-blue-300" />

        <span className="text-sm pt-2 pb-0 px-4 font-bold block w-full text-blue-900">
          Auth Layout
        </span>
        <Link
          to="/auth/login"
          className="text-sm py-2 px-4 font-normal block w-full bg-transparent text-blue-900 hover:bg-blue-300"
        >
          Login
        </Link>
        <Link
          to="/auth/register"
          className="text-sm py-2 px-4 font-normal block w-full bg-transparent text-blue-900 hover:bg-blue-300"
        >
          Register
        </Link>

        {/* Séparation */}
        <div className="h-0 mx-4 my-2 border border-solid border-blue-300" />

        <span className="text-sm pt-2 pb-0 px-4 font-bold block w-full text-blue-900">
          No Layout
        </span>
        <Link
          to="/landing"
          className="text-sm py-2 px-4 font-normal block w-full bg-transparent text-blue-900 hover:bg-blue-300"
        >
          Landing
        </Link>
        <Link
          to="/profile"
          className="text-sm py-2 px-4 font-normal block w-full bg-transparent text-blue-900 hover:bg-blue-300"
        >
          Profile
        </Link>
      </div>
    </>
  );
};

export default IndexDropdown;
