import React, { useState } from "react";
import { FaSwimmer, FaFutbol, FaBasketballBall, FaTableTennis, FaMapMarkerAlt, FaCalendarAlt, FaSearch } from "react-icons/fa";
const SearchBar = () => {
  const [activity, setActivity] = useState("");
  const [location, setLocation] = useState("");
  const [date, setDate] = useState("");
  const [isOpen, setIsOpen] = useState(false);

  const handleSearch = () => {
    console.log("Recherche effectuée avec :", { activity, location, date });
    // Ajoutez ici la logique de recherche
  };
    // Liste des activités avec leurs icônes
    const activities = [
        { value: "natation", label: "Natation", icon: <FaSwimmer className="text-blue-500 mr-2" /> },
        { value: "football", label: "Football", icon: <FaFutbol className="text-green-500 mr-2" /> },
        { value: "basketball", label: "Basketball", icon: <FaBasketballBall className="text-orange-500 mr-2" /> },
        { value: "tennis", label: "Tennis", icon: <FaTableTennis className="text-red-500 mr-2" /> },
      ];

  return (
    <div className="bg-white p-8 rounded-lg shadow-xl max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold mb-6 text-gray-800">Rechercher un terrain</h2>
      <div className="flex flex-col md:flex-row gap-4">
        {/* Sélecteur d'activité */}
        <select
          value={activity}
          onChange={(e) => setActivity(e.target.value)}
          className="border p-3 rounded-lg w-full md:w-1/4 focus:outline-none focus:ring-2 focus:ring-emerald-500 cursor-pointer"
        >
          <option value="">Activité</option>
          <option value="natation">Natation</option>
          <option value="football">Football</option>
          <option value="basketball">Basketball</option>
          <option value="tennis">Tennis</option>
        </select>

        {/* Sélecteur de localisation */}
        <select
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="border p-3 rounded-lg w-full md:w-1/4 focus:outline-none focus:ring-2 focus:ring-emerald-500 cursor-pointer"
        >
          <option value="">Localisation</option>
          <option value="tunis">Tunis</option>
          <option value="ariana">Ariana</option>
          <option value="ben_arous">Ben Arous</option>
          <option value="mannouba">Manouba</option>
        </select>

        {/* Sélecteur de date */}
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          className="border p-3 rounded-lg w-full md:w-1/4 focus:outline-none focus:ring-2 focus:ring-emerald-500 cursor-pointer"
        />

        {/* Bouton de recherche */}
        <button
          onClick={handleSearch}
          className="bg-emerald-600  px-6 py-3 rounded-lg hover: bg-emerald-500 transition-all duration-200 w-full md:w-auto cursor-pointer"
        >
          📌 Rechercher
        </button>
      </div>
    </div>
  );
};

export default SearchBar;
