import React, { useState } from "react";
import { FaSearch, FaMapMarkerAlt, FaCalendarAlt } from "react-icons/fa";

const SearchBar = () => {
  const [location, setLocation] = useState("");
  const [date, setDate] = useState("");

  const handleSearch = () => {
    console.log("Recherche effectuée avec :", { location, date });
    // Ajouter ici la logique de recherche
  };

  return (
    <div className="bg-gray-900 text-white p-6 rounded-lg shadow-lg flex flex-col md:flex-row items-center gap-4 w-full max-w-2xl mx-auto">
      {/* Localisation */}
      <div className="relative w-full md:w-1/3">
        <FaMapMarkerAlt className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
        <input
          type="text"
          placeholder="Gouvernorat..."
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="pl-10 pr-4 py-2 w-full bg-gray-800 border border-gray-600 rounded text-white focus:outline-none focus:ring-2 focus:ring-green-500"
        />
      </div>

      {/* Sélecteur de date */}
      <div className="relative w-full md:w-1/3">
        <FaCalendarAlt className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          className="pl-10 pr-4 py-2 w-full bg-gray-800 border border-gray-600 rounded text-white focus:outline-none focus:ring-2 focus:ring-green-500"
        />
      </div>

      {/* Bouton de recherche */}
      <button
        onClick={handleSearch}
        className="flex items-center justify-center gap-2 bg-green-600 text-white px-6 py-2 rounded w-full md:w-auto hover:bg-green-700 transition"
      >
        <FaSearch />
        Rechercher
      </button>
    </div>
  );
};

export default SearchBar;
