import React, { useState } from "react";
import { FaSwimmer, FaFutbol, FaBasketballBall, FaTableTennis, FaMapMarkerAlt, FaSearch, FaCalendarAlt } from "react-icons/fa";

const SearchBar = () => {
    const [activity, setActivity] = useState("");
    const [location, setLocation] = useState("");
    const [date, setDate] = useState("");
    const [showDropdown, setShowDropdown] = useState(false);
    const [showLocationDropdown, setShowLocationDropdown] = useState(false);

    const activities = [
        { value: "natation", label: "Natation", icon: <FaSwimmer className="text-blue-500" /> },
        { value: "football", label: "Football", icon: <FaFutbol className="text-green-500" /> },
        { value: "basketball", label: "Basketball", icon: <FaBasketballBall className="text-orange-500" /> },
        { value: "tennis", label: "Tennis", icon: <FaTableTennis className="text-red-500" /> },
    ];

    const cities = [
        { value: "tunis", label: "Tunis", icon: <FaMapMarkerAlt className="text-gray-500" /> },
        { value: "ariana", label: "Ariana", icon: <FaMapMarkerAlt className="text-gray-500" /> },
        { value: "ben_arous", label: "Ben Arous", icon: <FaMapMarkerAlt className="text-gray-500" /> },
        { value: "mannouba", label: "Manouba", icon: <FaMapMarkerAlt className="text-gray-500" /> },
    ];

    const handleSearch = () => {
        console.log("Recherche effectuée avec :", { activity, location, date });
    };

    return (
        <div className="bg-white/10 backdrop-blur-md p-4 rounded-2xl shadow-lg max-w-xs mx-auto text-white">
            <h2 className="text-lg font-semibold mb-2 text-center">Rechercher</h2>

            {/* Sélecteur de localisation */}
            <div className="relative mb-2">
                <button
                    onClick={() => setShowLocationDropdown(!showLocationDropdown)}
                    className="bg-transparent border border-white p-2 rounded-lg w-full flex items-center justify-between text-sm"
                >
                    {location ? cities.find((city) => city.value === location).icon : <FaMapMarkerAlt className="text-gray-400" />}
                    <span className="ml-2 flex-1 text-center">{location ? cities.find((city) => city.value === location).label : "Ville"}</span>
                </button>
                {showLocationDropdown && (
                    <ul className="absolute top-full left-0 w-full bg-white border rounded-md mt-1 shadow-md z-10 text-black">
                        {cities.map((city) => (
                            <li
                                key={city.value}
                                onClick={() => {
                                    setLocation(city.value);
                                    setShowLocationDropdown(false);
                                }}
                                className="p-2 flex items-center gap-2 cursor-pointer hover:bg-gray-100"
                            >
                                {city.icon} {city.label}
                            </li>
                        ))}
                    </ul>
                )}
            </div>

            {/* Sélecteur d'activité */}
            <div className="relative mb-2">
                <button
                    onClick={() => setShowDropdown(!showDropdown)}
                    className="bg-transparent border border-white p-2 rounded-lg w-full flex items-center justify-between text-sm"
                >
                    {activity ? activities.find((act) => act.value === activity).icon : <FaSearch className="text-gray-400" />}
                    <span className="ml-2 flex-1 text-center">{activity ? activities.find((act) => act.value === activity).label : "Activité"}</span>
                </button>
                {showDropdown && (
                    <ul className="absolute top-full left-0 w-full bg-white border rounded-md mt-1 shadow-md z-10 text-black">
                        {activities.map((act) => (
                            <li
                                key={act.value}
                                onClick={() => {
                                    setActivity(act.value);
                                    setShowDropdown(false);
                                }}
                                className="p-2 flex items-center gap-2 cursor-pointer hover:bg-gray-100"
                            >
                                {act.icon} {act.label}
                            </li>
                        ))}
                    </ul>
                )}
            </div>

            {/* Sélecteur de date */}
            <div className="relative mb-2">
                <div className="flex items-center border border-white p-2 rounded-lg">
                    <FaCalendarAlt className="text-gray-400" />
                    <input
                        type="date"
                        value={date}
                        onChange={(e) => setDate(e.target.value)}
                        className="bg-transparent w-full text-white text-center text-sm outline-none ml-2"
                    />
                </div>
            </div>

            {/* Bouton de recherche */}
            <button
                onClick={handleSearch}
                className="bg-yellow-500 px-4 py-2 rounded-lg hover:bg-yellow-400 transition-all duration-200 w-full flex items-center justify-center text-sm"
            >
                <FaSearch className="mr-2" /> Rechercher
            </button>
        </div>
    );
};

export default SearchBar;
