import React, { useState } from "react";
import { FaSwimmer, FaFutbol, FaBasketballBall, FaTableTennis, FaMapMarkerAlt, FaCalendarAlt, FaSearch } from "react-icons/fa";

const SearchBar = () => {
  const [activity, setActivity] = useState("");
  const [location, setLocation] = useState("");
  const [date, setDate] = useState("");

  const activities = {
    natation: <FaSwimmer className="text-blue-500 mr-2" />,
    football: <FaFutbol className="text-green-500 mr-2" />,
    basketball: <FaBasketballBall className="text-orange-500 mr-2" />,
    tennis: <FaTableTennis className="text-red-500 mr-2" />,
  };

  const locations = {
    tunis: "Tunis",
    ariana: "Ariana",
    ben_arous: "Ben Arous",
    mannouba: "Manouba",
  };

  const handleSearch = () => {
    console.log("Recherche effectuée avec :", { activity, location, date });
  };

  return (
    <div className="bg-white p-8 rounded-lg shadow-xl max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold mb-6 text-gray-800">Rechercher un terrain</h2>
      <div className="flex flex-col md:flex-row gap-4 items-center">
        
        {/* Sélecteur d'activité avec icônes */}
        <div className="relative w-full md:w-1/4">
          <select
            value={activity}
            onChange={(e) => setActivity(e.target.value)}
            className="border p-3 pl-10 rounded-lg w-full focus:outline-none focus:ring-2 focus:ring-emerald-500 cursor-pointer appearance-none"
          >
            <option value="">Activité</option>
            {Object.entries(activities).map(([key, icon]) => (
              <option key={key} value={key} className="flex items-center">
                {icon} {key.charAt(0).toUpperCase() + key.slice(1)}
              </option>
            ))}
          </select>
          <div className="absolute left-3 top-1/2 transform -translate-y-1/2">
            {activity ? activities[activity] : <FaSearch className="text-gray-400" />}
          </div>
        </div>

        {/* Sélecteur de localisation avec icône */}
        <div className="relative w-full md:w-1/4">
          <select
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            className="border p-3 pl-10 rounded-lg w-full focus:outline-none focus:ring-2 focus:ring-emerald-500 cursor-pointer appearance-none"
          >
            <option value="">Localisation</option>
            {Object.entries(locations).map(([key, name]) => (
              <option key={key} value={key}>{name}</option>
            ))}
          </select>
          <FaMapMarkerAlt className="absolute left-3 top-1/2 transform -translate-y-1/2 text-red-500" />
        </div>

        {/* Sélecteur de date avec icône */}
        <div className="relative w-full md:w-1/4">
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            className="border p-3 pl-10 rounded-lg w-full focus:outline-none focus:ring-2 focus:ring-emerald-500 cursor-pointer"
          />
          <FaCalendarAlt className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-500" />
        </div>

        {/* Bouton de recherche */}
        <button
          onClick={handleSearch}
          className="bg-emerald-600 text-white px-6 py-3 rounded-lg hover:bg-emerald-500 transition-all duration-200 w-full md:w-auto flex items-center justify-center"
        >
          <FaSearch className="mr-2" /> Rechercher
        </button>
      </div>
    </div>
  );
};

export default SearchBar;
