import React, { useState } from "react";
import { FaSwimmer, FaFutbol, FaBasketballBall, FaTableTennis, FaMapMarkerAlt, FaSearch } from "react-icons/fa";

const SearchBar = () => {
    const [activity, setActivity] = useState("");
    const [location, setLocation] = useState("");
    const [date, setDate] = useState("");
    const [showDropdown, setShowDropdown] = useState(false);
    const [showLocationDropdown, setShowLocationDropdown] = useState(false);

    const activities = [
        { value: "natation", label: "Natation", icon: <FaSwimmer className="text-blue-500" /> },
        { value: "football", label: "Football", icon: <FaFutbol className="text-green-500" /> },
        { value: "basketball", label: "Basketball", icon: <FaBasketballBall className="text-orange-500" /> },
        { value: "tennis", label: "Tennis", icon: <FaTableTennis className="text-red-500" /> },
    ];

    const cities = [
        { value: "tunis", label: "Tunis", icon: <FaMapMarkerAlt className="text-gray-500" /> },
        { value: "ariana", label: "Ariana", icon: <FaMapMarkerAlt className="text-gray-500" /> },
        { value: "ben_arous", label: "Ben Arous", icon: <FaMapMarkerAlt className="text-gray-500" /> },
        { value: "mannouba", label: "Manouba", icon: <FaMapMarkerAlt className="text-gray-500" /> },
    ];

    const handleSearch = () => {
        console.log("Recherche effectuée avec :", { activity, location, date });
    };

    return (
        <div className="bg-white/80 p-6 rounded-lg shadow-lg max-w-md mx-auto backdrop-blur-sm border border-gray-200"> {/* Arrière-plan semi-transparent, flou et bordure */}
            <h2 className="text-xl font-semibold mb-4 text-gray-800 text-center">Rechercher</h2>

            {/* Sélecteur de localisation (en haut) */}
            <div className="mb-4">
                <label className="block text-sm font-medium mb-1">Ville</label>
                <div className="relative">
                    <button
                        onClick={() => setShowLocationDropdown(!showLocationDropdown)}
                        className="bg-white/70 border p-2 pl-3 pr-6 rounded-md w-full text-left focus:outline-none focus:ring-2 focus:ring-emerald-500 cursor-pointer flex items-center justify-between text-sm" // Arrière-plan semi-transparent pour le bouton
                    >
                        {location
                            ? cities.find((city) => city.value === location).icon
                            : <FaMapMarkerAlt className="text-gray-400" />}
                        <span className="ml-2">
                            {location
                                ? cities.find((city) => city.value === location).label
                                : "Sélectionnez une ville"}
                        </span>
                    </button>

                    {showLocationDropdown && (
                        <ul className="absolute top-full left-0 w-full bg-white border rounded-md mt-1 shadow-md z-10 text-sm">
                            {cities.map((city) => (
                                <li
                                    key={city.value}
                                    onClick={() => {
                                        setLocation(city.value);
                                        setShowLocationDropdown(false);
                                    }}
                                    className="p-2 flex items-center gap-2 cursor-pointer hover:bg-gray-100"
                                >
                                    {city.icon} {city.label}
                                </li>
                            ))}
                        </ul>
                    )}
                </div>
            </div>

            {/* Ligne pour Activité et Date */}
            <div className="flex gap-4 mb-4">
                {/* Sélecteur d'activité */}
                <div className="flex-1">
                    <label className="block text-sm font-medium mb-1">Activité</label>
                    <div className="relative">
                        <button
                            onClick={() => setShowDropdown(!showDropdown)}
                            className="bg-white/70 border p-2 pl-3 pr-6 rounded-md w-full text-left focus:outline-none focus:ring-2 focus:ring-emerald-500 cursor-pointer flex items-center justify-between text-sm" // Arrière-plan semi-transparent pour le bouton
                        >
                            {activity
                                ? activities.find((act) => act.value === activity).icon
                                : <FaSearch className="text-gray-400" />}
                            <span className="ml-2">
                                {activity
                                    ? activities.find((act) => act.value === activity).label
                                    : "Sélectionnez une activité"}
                            </span>
                        </button>

                        {showDropdown && (
                            <ul className="absolute top-full left-0 w-full bg-white border rounded-md mt-1 shadow-md z-10 text-sm">
                                {activities.map((act) => (
                                    <li
                                        key={act.value}
                                        onClick={() => {
                                            setActivity(act.value);
                                            setShowDropdown(false);
                                        }}
                                        className="p-2 flex items-center gap-2 cursor-pointer hover:bg-gray-100"
                                    >
                                        {act.icon} {act.label}
                                    </li>
                                ))}
                            </ul>
                        )}
                    </div>
                </div>

                {/* Sélecteur de date */}
                <div className="flex-1">
                    <label className="block text-sm font-medium mb-1">Date</label>
                    <div className="relative">
                        <input
                            type="date"
                            value={date}
                            onChange={(e) => setDate(e.target.value)}
                            className="bg-white/70 border p-2 pl-3 rounded-md w-full focus:outline-none focus:ring-2 focus:ring-emerald-500 cursor-pointer text-sm" // Arrière-plan semi-transparent pour l'input
                        />
                    </div>
                </div>
            </div>

            {/* Bouton de recherche */}
            <button
                onClick={handleSearch}
                className="bg-yellow-500/80 px-4 py-2 rounded-md hover:bg-yellow-400 transition-all duration-200 w-full flex items-center justify-center text-sm" // Arrière-plan semi-transparent pour le bouton
            >
                <FaSearch className="mr-2" /> 
            </button>
        </div>
    );
};

export default SearchBar;
